var todayDate = '{!Employee_Reimbursement__c.Month__c}';
var str = todayDate.split("-");
var priMin = str[1]+'-'+str[0]+'-1';
var lastDayOfMonth = new Date(str[1], str[0], 0);
var priMax = str[1]+'-'+str[0]+'-'+lastDayOfMonth.getDate();
var fuel,  
fuel = '{!Employee_Reimbursement__c.Fuel_Price__c}';
if (fuel != "") {
    var fuelstr = fuel.split("$");
    fuel = fuelstr[1];
}
maintenansAndTires = '{!Employee_Reimbursement__c.Maintenance_and_Tires__c}';
if (maintenansAndTires != "") {
    var matstr = maintenansAndTires.split("$");
    maintenansAndTires = matstr[1];
}
var accounts = sforce.connection.query("SELECT id, Name,Contact_Id__c, Contact_Id__r.Account.Triplog_API__c FROM Employee_Reimbursement__c WHERE id = '{!Employee_Reimbursement__c.Id}'"); 
var apiToken = accounts.records.Contact_Id__r.Account.Triplog_API__c;
var contactids=accounts.records.Contact_Id__c;
if(apiToken != null && apiToken != '') {

    sforce.apex.execute("GetTripCallout","GetTripCalloutForMileage",{empReID:'{!Employee_Reimbursement__c.Id}', contactEmail:'{!Employee_Reimbursement__c.Contact_Email__c}',contactid:contactids,priMin:priMin,priMax:priMax, fuel:fuel, mpg:'{!Employee_Reimbursement__c.MPG__c}', maintenansAndTires:maintenansAndTires, apiToken:apiToken, tripStatus:'A', putCall:true});
    location.reload(true);
} else {
    alert('Please fill Account API Token');
}
